import React from "react";
import { Component } from 'react';

import withRoot from "./withRoot";
import {useQuery} from '@apollo/react-hooks';
import { useMutation } from '@apollo/react-hooks';

import gql from 'graphql-tag'
import { Button,Form } from 'semantic-ui-react'
import { ValuesOfCorrectType } from "graphql/validation/rules/ValuesOfCorrectType";
// import PostCard from


const GET_USER_QUERY = gql`
{
    user{
    email
    password
    username
}
}
`;


const USERDATA = gql`
    mutation createUser(
        $username: String!
        $email: String!
        $password: String!
    ){
        createUser(
            username : $username
            password : $password
            email : $email
        ){
            success
            errors
        }
}
`;


function Root(){

    const {data,loading,error} = useQuery(GET_USER_QUERY) 
    if (data){
        console.log(data)
    }
    if (error){
        console.log(error)
    }
    return(
        <center><h1>Small Demo</h1></center>
    )
}


function register(){



    const onChange = (event) => {
        setValues({...values, [event.target.name]: event.target.value});
    }

    const [ errors,setErrors] = useState({
    })

    const [values, setValues] = useState({
        username: '',
        password: '',
        email:''
    })


    const [addUser, {loading}] = useMutation(USERDATA, {
        update(proxy, result) {
            console.log(result)
        },
        onError(err){
            console.log(err)
            // setErrors({
            //     "message" : "Please insert valid credentials"
            // })
            setErrors(err.graphQlErrors[0].extensions.exception.errors)
        },
        variables : values
    })

    const onSubmit = (event) => {
        event.preventDefault()
        addUser();
    }






    return(
        <div>
            <center>
            <Form onSubmit={onSubmit}>

                <Form.Input
                    label="Username"
                    placeholder="Username"
                    name="username"
                    value={values.username}
                    onChange={onChange}
                    type="text"
                />
                <Form.Input
                    label="Email"
                    placeholder="Username"
                    name="username"
                    value={values.username}
                    onChange={onChange}
                    type="text"
                />
                <Form.Input
                    label="Username"
                    placeholder="Username"
                    name="username"
                    value={values.username}
                    onChange={onChange}
                    type="text"
                />


            </Form>
            </center>

        </div>

    )
}

export default withRoot(register);
