import{ Component } from 'react';

import withRoot from "./withRoot";
import {useQuery} from '@apollo/react-hooks';
import { useMutation } from '@apollo/react-hooks';

import gql from 'graphql-tag'
import { Grid } from 'semantic-ui-react'
// import PostCard from


const GET_USER_QUERY = gql`
{
    user{
    email
    password
    username
}
}
`;

function Root(){

        const {data,loading,error} = useQuery(GET_USER_QUERY) 
        if (data){
            console.log(data)
        }
        if (error){
            console.log(error)
        }
        return(
            <center><h1>Small Demo</h1></center>
        )
    }

function register(){

    const ADD_USER_MUTATION = gql`
    mutation createUsermutation($username: String!,email: String!,password: String!) {
      createUsermutation(username: $username, email: $email, password: $password)
      )
    }
  `;
    return (
    <div>
        <Form onSubmit={onSubmit} noValidate className={loading ? "loading" : ""}>
            <h1>Login Form</h1>
            <Form.Input
                label="Username or email"
                placeholder="Username or email"
                name="username"
                value={values.username}
                onChange={onChange}
                type="text"
            />
            <Form.Input
                label="Password"
                placeholder="Password"
                name="password"
                onChange={onChange}
                value={values.password}
                type="password"
            />

            {Object.keys(errors).length > 0 && (
                <div className="ui error message">
                    <ul className="list">
                        {Object.keys(errors).map(value => (
                            <li key={value}>{value}</li>
                        ))}
                    </ul>
                </div>
            )}
            <Button type='submit'>Login</Button>
        </Form>
    </div>
);

}




export default withRoot(register);
