              <Grid.Column>
                <Image src='/images/wireframe/media-paragraph.png' />
              </Grid.Column>
            </Grid.Row>
            </Grid>
        )
    }


const GET_USER_QUERY = gql`
{
    user{
    email
    password
    username
}
}
`;

export default withRoot(Root);
